import React, { useState, useEffect } from "react";
import SillonDataService from "../services/SillonService";

const Sillon = props => {
    const initialSillonState = {
        id: null,
        tipo: "",
        activo: true,
        codigo: ""
    };
    const [currentSillon, setCurrentSillon] = useState(initialSillonState);
    const [message, setMessage] = useState("");

    const getSillon = id => {
        SillonDataService.get(id)
            .then(response => {
                setCurrentSillon(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    useEffect(() => {
        getSillon(props.match.params.id);
    }, [props.match.params.id]);

    const handleInputChange = event => {
        const { name, value } = event.target;
        setCurrentSillon({ ...currentSillon, [name]: value });
    };

    const updateActivo = status => {
        var data = {
            id: currentSillon.id,
            tipo: currentSillon.tipo,
            codigo: currentSillon.codigo,
            activo: status
        };

        SillonDataService.update(currentSillon.id, data)
            .then(response => {
                setCurrentSillon({ ...currentSillon, activo: status });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    const updateSillon = () => {
        SillonDataService.update(currentSillon.id, currentSillon)
            .then(response => {
                console.log(response.data);
                setMessage("El sillÃ³n ha sido actualizado correctamente.");
            })
            .catch(e => {
                console.log(e);
            });
    };

    return (
        <div>
            {currentSillon ? (
                <div className="edit-form">
                    <h4>Editar SillÃ³n</h4>
                    <form>
                        <div className="form-group">
                            <label htmlFor="codigo">Nombre SillÃ³n</label>
                            <input
                                type="text"
                                className="form-control"
                                id="codigo"
                                name="codigo"
                                value={currentSillon.codigo}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="tipo">Tipo de SillÃ³n</label>
                            <input
                                type="text"
                                className="form-control"
                                id="tipo"
                                name="tipo"
                                value={currentSillon.tipo}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="form-group">
                            <label>
                                <strong>Estado: </strong>
                            </label>
                            {currentSillon.activo ? " Activo" : " No Activo"}
                        </div>
                    </form>

                    <button
                        type="submit"
                        className="btn btn-primary"
                        onClick={updateSillon}>
                        Actualizar
                        </button>

                    <p>{message}</p>
                </div>
            ) : (
                    <div>
                        <br />
                        <p>Seleccione un SillÃ³n para ver mÃ¡s detalles</p>
                    </div>
                )}
        </div>
    );



};

export default Sillon;
