import React, { useState, useEffect } from "react";
import SillonDataService from "../services/SillonService";
import { Link } from "react-router-dom";

const SillonList = () => {
    const [sillones, setSillones] = useState([]);
    const [currentSillon, setCurrentSillon] = useState(null);
    const [currentIndex, setCurrentIndex] = useState(-1);
    //dejado como idea, aun no sabemos como implementar busqueda.
    //const [searchTitle, setSearchTitle] = useState("");

    useEffect(() => {
        retrieveSillones();
    }, []);

    /*
    const onChangeSearchTitle = e => {
        const searchTitle = e.target.value;
        setSearchTitle(searchTitle);
    };
    */
    const retrieveSillones = () => {
        SillonDataService.getAll()
            .then(response => {
                setSillones(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    /*
    const refreshList = () => {
        retrieveSillones();
        setCurrentSillon(null);
        setCurrentIndex(-1);
    };
    */

    const setActiveSillon = (sillon, index) => {
        setCurrentSillon(sillon);
        setCurrentIndex(index);
    };

    /*
    const findByTitle = () => {
    TutorialDataService.findByTitle(searchTitle)
      .then(response => {
        setTutorials(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };
    */

    return (
        <div className="list row">
            <div className="col-md-12">
                <div className="mb-3">
                    <h1>Listado de Sillones</h1>
                    Acá iría una busqueda
                </div>
            </div>
            <div className="col-md-2"></div>
            <div className="col-md-8">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">Nombre</th>
                            <th scope="col">Tipo</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {sillones &&
                            sillones.map((sillon) => (
                                <tr key={sillon.id}>
                                    <td>{sillon.codigo}</td>
                                    <td>{sillon.tipo}</td>
                                    <td>
                                        <Link
                                            to={"/sillones/" + sillon.id}
                                            className="btn btn-primary"
                                        >
                                            Editar
                                        </Link>
                                    </td>
                                </tr>
                            ))}
                    </tbody>
                </table>
                <Link to={"/add"} className="btn btn-primary">
                Nuevo Sillón
              </Link>
            </div>
            <div className="col-md-2"></div>
        </div>
    );
};

export default SillonList;
