import React, { useState, useEffect } from "react";
import SillonDataService from "../services/SillonService";
import { Link } from "react-router-dom";

const SillonList = props => {
    const [sillones, setSillones] = useState([]);
    const [waitingQuery, setWaitingQuery] = useState(true);
    const [isDeleted, setIsDeleted] = useState(false);
    const [tipoFilter, setTipoFilter] = useState("");
    const [activoFilter, setActivoFilter] = useState(true);
    //dejado como idea, aun no sabemos como implementar busqueda.
    //const [searchTitle, setSearchTitle] = useState("");

    useEffect(() => {
        retrieveSillones();
    }, []);

    /*
    const onChangeSearchTitle = e => {
        const searchTitle = e.target.value;
        setSearchTitle(searchTitle);
    };
    */
    const retrieveSillones = () => {
        SillonDataService.getAll()
            .then(response => {
                setSillones(response.data);
                setWaitingQuery(false);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    const retrieveFiltered = () => {
        console.log(this.refs.tipoFilter);
        console.log(this.refs.activoFilter.checked);
    }

    const refreshList = () => {
        setWaitingQuery(true);
        retrieveSillones();
    };

    const deleteSillon = id => {
        SillonDataService.remove(id)
            .then(response => {
                console.log(response.data);
                refreshList();
            })
            .catch(e => {
                console.log(e);
            });
    };

    const setActivoSillon = sillon => {
        SillonDataService.update(sillon.id, sillon)
            .then(response => {
                console.log(response.data);
                refreshList();
            })
            .catch(e => {
                console.log(e);
            });
    };

    /*
    const findByTitle = () => {
    TutorialDataService.findByTitle(searchTitle)
      .then(response => {
        setTutorials(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };
    */

    return (
        <div className="list row">
            <div className="col-md-12">
                <div className="mb-3">
                    <h1>Listado de Sillones</h1>
                    <form>
                        <div className="form-row align-items-center">
                            <div className="col-auto">
                                <div className="input-group mb-2">
                                    <div className="input-group-prepend">
                                        <div className="input-group-text">Tipo de Sillón:</div>
                                    </div>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="tipo"
                                        name="tipo"
                                        ref="tipoFilter"                                  
                                    />
                                </div>
                            </div>
                            <div className="col-auto">
                                <div className="form-check mb-2">
                                    <input
                                        className="form-check-input"
                                        type="checkbox"
                                        id="activo"
                                        name="activo"
                                        ref="activoFilter"
                                    />
                                    <label className="form-check-label" for="activo">
                                        Obtener Sillones Eliminados
                                    </label>
                                </div>
                            </div>
                            <div className="col-auto">
                                
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <div className="col-md-12">
                {!waitingQuery ? (
                    <div className="overflow-auto">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Tipo</th>
                                    <th scope="col" className="text-center">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sillones &&
                                    sillones.map((sillon) => (
                                        <tr key={sillon.id}>
                                            <td>{sillon.codigo}</td>
                                            <td>{sillon.tipo}</td>
                                            <td>
                                                <div className="d-flex justify-content-center">
                                                    {isDeleted ? (
                                                        <div>
                                                            <button className="btn btn-warning m-1" onClick={() => setActivoSillon(sillon)}>
                                                                Eliminar
                                                            </button>
                                                        </div>
                                                    ) : (
                                                            <div>
                                                                <Link
                                                                    to={"/sillones/" + sillon.id}
                                                                    className="btn btn-primary m-1"
                                                                >
                                                                    Editar
                                                                </Link>
                                                                <button className="btn btn-danger m-1" onClick={() => deleteSillon(sillon.id)}>
                                                                    Eliminar
                                                                </button>
                                                            </div>
                                                        )}
                                                </div>
                                            </td>
                                        </tr>
                                    ))}
                            </tbody>
                        </table>
                    </div>
                ) : (
                        <div className="d-flex justify-content-center m-2">
                            <div className="spinner-border text-primary mx-2" role="status">
                                <span className="sr-only">Loading...</span>
                            </div>
                            <strong>Cargando, un momento...</strong>
                            <br />
                        </div>
                    )
                }

                <Link to={"/add"} className="btn btn-primary mt-2">
                    Nuevo Sillón
              </Link>
            </div >
        </div >
    );
};

export default SillonList;
